cim:FunctionDescriptor.cim:FunctionDescriptor.equation:
equation
	if EPC_Blocking_A then
		P_incA = pre(P_incA);
	else
		P_incA = P;
	end if;

	if EPC_Blocking_B then
		P_incB = pre(P_incB);
	else
		P_incB = P;
	end if;

	if EPC_Blocking_A and ((ori_block_A and P_incA>0) or (not(ori_block_A) and P_incA<=0)) then
		surpress_A = 1;
	else
		surpress_A = 0;
	end if;

	if (Flipflop(EPC2 and (not(EPC4)),EPC4) = true then
		epc2_start = delay(1,0.1);
	else if (Flipflop(EPC2 and (not(EPC4)),EPC4) = false then
		epc2_start = delay(0,0.0);
	else
		epc2_start = pre(epc2_start)
	end if;	

	if (Flipflop(EPC3 and (not(EPC4)),EPC4) = true then
		epc3_start = delay(1,0.1);
	else if (Flipflop(EPC3 and (not(EPC4)),EPC4) = false then
		epc3_start = delay(0,0.0);
	else
		epc3_start = pre(epc3_start)
	end if;	

	if (Flipflop(EPC5 and (not(EPC8)) and (not (epc6_active or epc7_active)) and (not (surpress_A))),EPC8) then
		epc5_start = delay(1,T_delay);
	else
		epc5_start = delay(0,0.0);
	end if;	

	if (Flipflop(EPC6 and (not(EPC8)) and (not (epc5_active or epc7_active)) and (not (surpress_A))),EPC8) then
		epc6_start = delay(1,T_delay);
	else
		epc6_start = delay(0,0.0);
	end if;	

	if (Flipflop(EPC7 and (not(EPC8)) and (not (epc5_active or epc6_active)) and (not (surpress_A))),EPC8) then
		epc7_start = delay(1,T_delay);
	else
		epc7_start = delay(0,0.0);
	end if;	


epc1_active = Flipflop(EPC1 and (not(Picdro(Flipflop(EPC1, not(EPC1))),0.01,0.0)),(Picdro(Flipflop(EPC1,not(Pickdro(EPC1,0.0,2.0))),1.005,0.0));

	if epc1_active then
		 print("EPC Function 1 activated.");
	end if;
	if epc2_start then
		 print("EPC Function 2 activated.");
	end if;
	if epc3_start then
		 print("EPC Function 3 activated.");
	end if;
	if EPC4 then
		 print("EPC Function 4 activated.");
	end if;
	if epc5_start then
		 print("EPC Function 5 activated.");
	end if;
	if epc6_start then
		 print("EPC Function 6 activated.");
	end if;
	if epc7_start then
		 print("EPC Function 7 activated.");
	end if;
	if EPC8 then
		 print("EPC Function 8 activated.");
	end if;
	if (EPC_Blocking_A and surpress_A) then
		 print("EPC Function Blocking A activated.");
	end if;
	if (EPC_Blocking_A and (not(surpress_A))) then
		 print("EPC Function Blocking A activated, but ignored because of power flow direction.");
	end if;
	if EPC_Blocking_B then
		 print("EPC Function Blocking B activated.");
	end if;

epc2_active = epc2_start or Pmax_epc2<Pmax_inc or Pmax_epc2>Pmax_inc;
epc3_active = epc3_start or Pmax_epc3<Pmax_inc or Pmax_epc3>Pmax_inc;

epc5_active = Picdro((epc5_start and (dP_epc5<P_EPC5/Sr or dP_epc5>P_EPC5/Sr)), 0.0, T_hold);
epc6_active = Picdro((epc6_start and (dP_epc6<P_EPC6/Sr or dP_epc6>P_EPC6/Sr)), 0.0, T_hold);
epc7_active = Picdro((epc7_start and (dP_epc7<P_EPC7/Sr or dP_epc7>P_EPC7/Sr)), 0.0, T_hold);

EPC_flag = Picdro(epc1_active,0.0,1.0) or epc2_active or epc3_active or EPC4 or epc5_start or epc6_start or epc7_start or epc5_active or epc6_active or epc7_active or EPC8 or (Flipflop((dP_epc<-0.0001  or  dP_epc>0.0001), (dP_epc>-0.0001  and  dP_epc<0.0001)));

Pmax_epc1 = GradLim(Select(epc1_active, 0.0, Pmax_inc), -250, 1);
Pmin_epc1 = -Pmax_epc1;
Pmax_epc2 = GradLim(Select(epc2_start, 0.50, Pmax_inc), -dP_EPC2/Sr, 0.1/60);
Pmin_epc2 = -Pmax_epc2;
Pmax_epc3 = GradLim(Select(epc3_start, 0.25, Pmax_inc), -dP_EPC3/Sr, 0.1/60);
Pmin_epc3 = -Pmax_epc3;
dP_epc5 = GradLim(Select(epc5_start, P_EPC5/Sr, 0), -dP_EPC5/Sr, dP_EPC5/Sr);
dP_epc6 = GradLim(Select(epc6_start, P_EPC6/Sr, 0), -dP_EPC6/Sr, dP_EPC6/Sr);
dP_epc7 = GradLim(Select(epc7_start, P_EPC7/Sr, 0), -dP_EPC7/Sr, dP_EPC7/Sr);

dP_epc = dP_epc5+dP_epc6+dP_epc7;

Pmin_epc = Select(EPC_Blocking_B  and  P_incB>0  and  (epc5_start or epc6_start or epc7_start or EPC8), 0.0, max(Pmin_inc,max(Pmin_epc1,max(Pmin_epc2,Pmin_epc3))));
Pmax_epc = Select(EPC_Blocking_B  and  P_incB<=0  and  {epc5_start or epc6_start or epc7_start or EPC8}, 0.0, min(Pmax_inc,min(Pmax_epc1,min(Pmax_epc2,Pmax_epc3))));

function Flipflop
	input  Boolean set;
	input  Boolean reset;
	output Boolean state;
algorithm
	if (set = true) and (reset = false) then
		state := true;
	end if;
	if (set = false) and (reset = true) then
		state := false;
	end if;	
end Flipflop;

function Picdro
	input Boolean boolexpr;
	input Real Tpick;
	input Real Tdrop;
	output Boolean varA;
algorithm
	if boolexpr = true then
		varA = delay(1,Tpick);
	else if boolexpr = false then
		varA = delay(0,Tdrop);
	else
		varA = pre(varA)
	end if;
end Picdro;

function GradLim
	input Real inputSignal;
	input Real minGradient;
	input Real maxGradient;
	output Real outputSignal;
algorithm
	if der(inputSignal) > maxGradient then
		outputSignal = maxGradient+inputSignal;
	else if der(inputSignal) < minGradient then
		outputSignal = minGradient+inputSignal;
	else
		outputSignal = inputSignal+der(inputSignal);
	end if;
end GradLim;

function Select
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real outputexpr;
algorithm
	if boolexpr then
		outputexpr = x;
	else
		outputexpr = y;
	end if;
end Select;

