Real dP_epc7 (start = 0);
Real dP_epc (start = 0);
Real Pmin_epc;
    if noEvent(abs(Pmin_epc))) < 1e-6 then
    Pmin_epc = -1;
end if;
Real Pmax_epc;
    if noEvent(abs(Pmax_epc))) < 1e-6 then
    Pmax_epc = 1;
end if;
