model P/Vdc_Control_Vdc_ref_STATCOM_EPC_input

equation
	P_ref_freeze = Select(EPC,pre(P_ref_freeze), P_ref_in);
	P_ref_out = Select(STATCOM_on or trip_dc, 0, Select(EPC, P_ref_freeze+dP_epc, P_ref_in));

end P/Vdc_Control_Limiter;

function Select
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real varA;
algorithm
	if boolexpr then
		varA = x;
	else
		varA = y;
	end if;
end Select;

cim:FunctionDescriptor.cim:FunctionDescriptor.initialConditions:
Real P_ref (start = P-dP_fsm-dP_pod);
Real xiP (start = Selfix(Ctrl_P,iP_ref*Selfix(Mode_Vadp,max(uac_loc,Vac_min),1.0)-dP_si-Kp_P*P,iP_ref*Selfix(Mode_Vadp,max(uac_loc,Vac_min),1.0)-dP_si));
if noEvent(abs(UVRT))) < 1e-6 then
    UVRT = 0;
end if;
if noEvent(abs(block))) < 1e-6 then
    block = 0;
end if;
if noEvent(abs(trip_dc))) < 1e-6 then
    trip_dc = 0;
end if;
if noEvent(abs(STATCOM_on))) < 1e-6 then
    STATCOM_on = 0;
end if;
if noEvent(abs(iP_in_limit))) < 1e-6 then
    iP_in_limit = 0;
end if;
if noEvent(abs(dP_fsm))) < 1e-6 then
    dP_fsm = 0;
end if;
if noEvent(abs(dP_pod))) < 1e-6 then
    dP_pod = 0;
end if;
if noEvent(abs(dP_si))) < 1e-6 then
    dP_si = 0;
end if;
Real EPC (start = 0);
Real dP_epc (start = 0);
Real P_max_epc (start = max(P_max,P_ref_max));
Real P_min_epc (start = min(P_min,P_ref_min));
if noEvent(abs(iP_ref))) < 1e-6 then
    iP_ref = 0;
end if;

