model P/Vdc_Control_Switch_SI_EPC

equation
	yo = Selfix(SI_epc, yi, Select(EPC, 0.0, yi));
end P/Vdc_Control_Switch_SI_EPC;

function Selfix
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real outputexpr;
	parameter Real initializedVariable;
algorithm
	initializedVariable = conditionalInitialization(boolexpr);
	if initializedVariable then
		outputexpr = x;
	else
		outputexpr = y;
	end if;
end Selfix;

function Select
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real varA;
algorithm
	if boolexpr then
		varA = x;
	else
		varA = y;
	end if;
end Select;

