function Lim
	input Real x;
	input Real minLim;
	input Real maxLim;
	output Real yo;
algorithm
	if x < minLim then
		yo = minLim;
	else if x > maxLim then
		yo = maxLim;
	else
		yo = x;
	end if;
end Lim;

