model P/Vdc_Control_Gradient_Limiter

equation
	yo = Selfix(gradlim_on, GradLim(Selfix(gradlim_on,yi,0),(dy_min/100),(dy_max/100)), yi);

end P/Vdc_Control_Gradient_Limiter;

function Selfix
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real outputexpr;
	parameter Real initializedVariable;
algorithm
	initializedVariable = conditionalInitialization(boolexpr);
	if initializedVariable then
		outputexpr = x;
	else
		outputexpr = y;
	end if;
end Selfix;

function GradLim
	input Real inputSignal;
	input Real minGradient;
	input Real maxGradient;
	output Real outputSignal;
algorithm
	if der(inputSignal) > maxGradient then
		outputSignal = maxGradient+inputSignal;
	else if der(inputSignal) < minGradient then
		outputSignal = minGradient+inputSignal;
	else
		outputSignal = inputSignal+der(inputSignal);
	end if;
end GradLim;

model P/Vdc_Control_PI_Regulator

equation
	// Ctrl = 0 => Parallel PI Regulator: H(s) = Kp + Ki/s
	// Ctrl = 1 => I Regulator (H(s) = Ki/s) with parallel proportional reference feed-forward (H(s) = Kp)

	Kp = Kp_P;
	Ki = Ki_P;
	yp = Selfix(Ctrl_P,yi_ref,yi_error);
	y_max = min(P_max,P_max_epc);
	y_min = max(P_min,P_min_epc);
	yx_max = P_max;
	yx_min = P_min;                                                                                                                   
	freeze_int = Picdro(block or UVRT or (iP_in_limit and ((xiP>0 and yi_error>0) or (xiP<0 and yi_error<0))), 0.000, 0.010);

	der(xiP) = Select(freeze_int, 0.0, Select((Flipflop(pre(xiP)+Lim(Kp*yp,min(-y_max,y_min),max(-y_min,y_max))>y_max,pre(xiP)+Lim(Kp*yp,min(-y_max,y_min),max(-y_min,y_max))<y_max) and Flipflop(yi_error>0.0,yi_error<0.0)) or (Flipflop(pre(xiP)+Lim(Kp*yp,min(-y_max,y_min),max(-y_min,y_max))<y_min,pre(xiP)+Lim(Kp*yp,min(-y_max,y_min),max(-y_min,y_max))>y_min) and Flipflop(yi_error<0.0,yi_error>0.0)), 0.0, Ki*yi_error));   // normal operation mode
	yo = Lim(LimState(xiP,yx_min-0.0000001,yx_max+0.0000001)+Kp*yp,y_min,y_max);

end P/Vdc_Control_PI_Regulator;

function Select
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real varA;
algorithm
	if boolexpr then
		varA = x;
	else
		varA = y;
	end if;
end Select;

function Selfix
	input Boolean boolexpr;
	input Real x;
	input Real y;
	output Real outputexpr;
	parameter Real initializedVariable;
algorithm
	initializedVariable = conditionalInitialization(boolexpr);
	if initializedVariable then
		outputexpr = x;
	else
		outputexpr = y;
	end if;
end Selfix;

function Picdro
	input Boolean boolexpr;
	input Real Tpick;
	input Real Tdrop;
	output Boolean varA;
algorithm
	if boolexpr = true then
		varA = delay(1,Tpick);
	else if boolexpr = false then
		varA = delay(0,Tdrop);
	else
		varA = pre(varA)
	end if;
end Picdro;

function Flipflop
	input  Boolean boolset;
	input  Boolean boolreset;
	output Boolean state;
algorithm
	if (boolset = true) and (boolreset = false) then
		state := true;
	end if;
	if (boolset = false) and (boolreset = true) then
		state := false;
	end if;	
end Flipflop;

function Lim
	input Real x;
	input Real minLim;
	input Real maxLim;
	output Real yo;
algorithm
	if x < minLim then
		yo = minLim;
	else if x > maxLim then
		yo = maxLim;
	else
		yo = x;
	end if;
end Lim;

function LimState
	input Real x1 (stateSelect=StateSelect.always);
	input Real xmin;
	input Real xmax;
	output Real yo;
algorithm
	domainType = 1; // 1 for RMS, 0 for EMT simulation
	if x < xmin then
		yo = xmin;
	else if x > xmax then
		yo = xmax;
	else
		yo = x1;
	end if;
end LimState;

